import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';


function Square(props) {
  return (
    <button className="square" onClick={props.onClick} >
      {props.value}
    </button>
  );
}
var scoreA = 0;
var scoreB = 0;
var sb = Array(4).fill(0);
var movesleft = 4;
class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill(null),
      xIsNext: true,
    };
  }

  handleClick(i) {
    const squares = this.state.squares.slice();
    if (calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? 'A-> '+sb[i] : 'B-> '+sb[i];
	if(this.state.xIsNext == 0){
		scoreB = (scoreB*1.0 + (sb[i]*1.0));
	}
	else{
		scoreA = ((1.0*scoreA) + (sb[i]*1.0));
	}
	movesleft = (movesleft*1.0)-1;
    this.setState({
      squares: squares,
      xIsNext: !this.state.xIsNext,
    });
  }

  renderSquare(i) {
	 if (sb[i]){
		 const squares = this.state.squares.slice();
		 if (squares[i]){
			 return (
		  <Square
			value={squares[i]}
			onClick={() => this.handleClick(i)}
		  />
		);
		 }
		 return (
		  <Square
			value={sb[i]}
			onClick={() => this.handleClick(i)}
		  />
		);
	 }
	const min = 1;
    const max = 100;
    const rand = min + Math.random() * (max - min);
	sb[i]=rand.toFixed(0);
    return (
      <Square
        value={rand.toFixed(0)}
        onClick={() => this.handleClick(i)}
      />
    );
  }

  render() {
    const winner = calculateWinner(this.state.squares);
    let status;
	let abc;
    if (winner) {
      status = 'Result: ' + winner;
    } else {
      status = 'Next player: ' + (this.state.xIsNext ? 'A' : 'B');
	  abc = 'score A: '+scoreA + ' B: '+scoreB;
    }

    return (
      <div>
        <div className="status">{status}</div>
		<div className="abc"> {abc} </div>
        <div className="board-row">
          {this.renderSquare(0)}
		  <br />
		  <br />
          {this.renderSquare(1)}
		  <br />
		  <br />
          {this.renderSquare(2)}
		  <br />
		  <br />
		  {this.renderSquare(3)}
		  <br />
		  <br />
        </div>
      </div>
    );
  }
}

class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);

function calculateWinner(squares) {
	if( movesleft == 0){
		if(scoreA > scoreB){
			return 'A';
		}
		else if (scoreA == scoreB){
			return 'Draw';
		}
		else{
			return 'B';
		}
	}
	return null;
}

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister();
