import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import  get  from 'lodash/get';
import  size  from 'lodash/size';
import forEach from 'lodash/forEach';
import  isEmpty  from 'lodash/isEmpty';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Table from 'react-bootstrap/Table';
import Alert from 'react-bootstrap/Alert';
import * as serviceWorker from './serviceWorker';
import { KeyObject } from 'crypto';

export class Covid extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
			covidData: {}
    };
	}

	getTableBody() {
		let tableData = [];
		forEach(this.state.covidData, (data, key) => {
			const casesToday = `${data.cases} | Cases Today : ${data.todayCases}`;
			const deathsToday = `${data.deaths} | Deaths Today : ${data.todayDeaths}`;
			tableData.push(
				<tr>
					<td><Alert variant="success">
  <Alert.Heading>{data.country} </Alert.Heading>
  <hr />

</Alert></td>

<td><Alert variant="primary">
  <Alert.Heading>{data.cases} </Alert.Heading>
  <hr />
  <p className="sm-5">
    {casesToday}
  </p>
</Alert></td>
<td><Alert variant="primary">
  <Alert.Heading>{data.recovered} </Alert.Heading>
  <hr />

</Alert>
	</td>
<td><Alert variant="primary">
  <Alert.Heading>{data.deaths} </Alert.Heading>
  <hr />
  <p className="sm-5">
    {deathsToday}
  </p>
</Alert></td>
</tr>
			);
	});
		return tableData;
	}

	getHeader() {
		return(
			<div>
				<Container fluid="md">
					<Row>
						<Col md>CoronaVirus Tracker</Col>
					</Row>
				</Container>
				<div className = "covid__table">
				<Table 
				striped bordered hover 
				variant="dark"
				responsive
				size="sm">
				 <thead>
					<tr>
						<th>Country</th>
						<th>Total Cases</th>
						<th>Recoverd</th>
						<th>Deaths</th>
					</tr>
				 </thead>
				 <tbody>
					 {this.getTableBody()}
				 </tbody>
				</Table>
				</div>
			</div>
		);
	}

  render(){

		/*let options = [];
		if (!isEmpty(covid)) {
			forEach(covid, (data, value) => {
					console.log(data, value);
					console.log("jell");
			});
		}*/

	  return (
			<div className="covid__body">
				{this.getHeader()}

			</div>
    );
	}
	componentDidMount(){
		document.title = "Covid Update";
		/*fetch('https://pomber.github.io/covid19/timeseries.json')
		.then(results => {
				return results.json();
		}).then(data => {
			this.setState({
				covidData: data
			});
		});*/

		fetch('https://coronavirus-19-api.herokuapp.com/countries')
		.then(results => {
				return results.json();
		}).then(data => {
			this.setState({
				covidData: data
			});
			console.log(data);
		});
  }
}

ReactDOM.render(
  <Covid />,
  document.getElementById('root')
);

serviceWorker.unregister();
